// Setup multer for file uploads
const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req: any, file: any, cb: any) => {
    if (file.mimetype.startsWith('image/')) {
      cb(null, true);
    } else {
      cb(new Error('Only image files are allowed'));
    }
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Serve static files from attached_assets
  const path = await import("path");
  const express = await import("express");
  app.use('/attached_assets', express.default.static(path.default.join(process.cwd(), 'attached_assets')));

  // Session middleware
  app.use(sessionConfig);

  // Auth routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const userData = registerSchema.parse(req.body);
      const user = await storage.register(userData);
      req.session.userId = user.id;
      res.json({ user, message: "Registration successful" });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid data", details: error.errors });
      }
      if (error instanceof Error && error.message.includes("duplicate key")) {
        return res.status(409).json({ error: "Email already registered" });
      }
      console.error("Registration error:", error);
      res.status(500).json({ error: "Registration failed" });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const credentials = loginSchema.parse(req.body);
      const user = await storage.login(credentials);

      if (!user) {
        return res.status(401).json({ error: "Invalid email or password" });
      }

      req.session.userId = user.id;
      res.json({ user, message: "Login successful" });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid data", details: error.errors });
      }
      console.error("Login error:", error);
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ error: "Logout failed" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  // Get current user
  app.get("/api/auth/user", async (req, res) => {
    try {
      if (!req.session.userId) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const user = await storage.getUserById(req.session.userId);
      if (!user) {
        return res.status(401).json({ error: "User not found" });
      }

      res.json(user);
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ error: "Failed to get user" });
    }
  });



  // Categories
  app.get("/api/categories", async (req, res) => {
    try {
      const { type } = req.query;
      const categories = await storage.getCategories(type ? String(type) : undefined);
      res.json(categories);
    } catch (error) {
      console.error("Get categories error:", error);
      res.status(500).json({ error: "Failed to fetch categories" });
    }
  });

  app.get("/api/categories/:slug", async (req, res) => {
    try {
      const { slug } = req.params;
      const category = await storage.getCategoryBySlug(slug);
      if (!category) {
        return res.status(404).json({ error: "Category not found" });
      }
      res.json(category);
    } catch (error) {
      console.error("Get category error:", error);
      res.status(500).json({ error: "Failed to fetch category" });
    }
  });

  // Image upload routes
  // Simple image upload without authentication for testing
  app.post("/api/images/upload-simple", upload.array('images', 8), async (req: any, res) => {
    try {
      if (!req.files || !Array.isArray(req.files) || req.files.length === 0) {
        return res.status(400).json({ error: "No images provided" });
      }

      const imageUrls: string[] = [];

      for (const file of req.files) {
        // Generate unique filename
        const imageId = `img_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        const imagePath = `/api/images/${imageId}`;

        // Standardize image format for marketplace consistency
        let processedBuffer: Buffer;
        try {
          processedBuffer = await ImageProcessor.standardizeForMarketplace(file.buffer, {
            maxWidth: 800,
            maxHeight: 600,
            quality: 85,
            format: "jpeg"
          });
        } catch (processingError) {
          console.warn("Image processing failed, using original:", processingError);
          processedBuffer = file.buffer;
        }

        // Store standardized image buffer in memory
        imageStorage.set(imageId, processedBuffer);
        imageUrls.push(imagePath);
      }

      res.json({
        success: true,
        imageUrls,
        message: `${req.files.length} image(s) uploaded and standardized successfully`,
      });

    } catch (error) {
      console.error("Error uploading images:", error);
      res.status(500).json({ 
        error: "Failed to upload images",
        details: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  app.get("/api/images/:imageId", async (req, res) => {
    try {
      const { imageId } = req.params;

      // First try to get from memory storage
      const imageBuffer = imageStorage.get(imageId);
      if (imageBuffer) {
        res.set({
          'Content-Type': 'image/jpeg',
          'Cache-Control': 'public, max-age=3600',
          'X-Image-Standardized': 'true' // Indicate image has been processed
        });
        return res.send(imageBuffer);
      }

      // Image not found in memory storage
      return res.status(404).json({ error: "Image not found" });

    } catch (error) {
      console.error("Get image error:", error);
      res.status(500).json({ error: "Failed to get image" });
    }
  });

  // Posts
  app.get("/api/posts", optionalAuth, async (req, res) => {
    try {
      const { categoryId, type, isFeatured, limit, search, location, minPrice, maxPrice, store } = req.query;

      const options: any = {};
      if (categoryId && categoryId !== 'all') options.categoryId = String(categoryId);
      if (type && type !== 'all') options.type = String(type);
      if (isFeatured !== undefined) options.isFeatured = isFeatured === 'true';
      if (limit) options.limit = parseInt(String(limit));
      if (search) options.search = String(search);
      if (location) options.location = String(location);
      if (minPrice) options.minPrice = parseFloat(String(minPrice));
      if (maxPrice) options.maxPrice = parseFloat(String(maxPrice));
      if (store) options.store = String(store);
      if (req.session.userId) options.userId = req.session.userId;

      const posts = await storage.getPosts(options);
      res.json(posts);
    } catch (error) {
      console.error("Get posts error:", error);
      res.status(500).json({ error: "Failed to fetch posts" });
