import { useState } from "react";
import { Search, Filter, X, ChevronDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { useQuery } from "@tanstack/react-query";
import type { Category } from "@shared/schema";

interface SearchFilters {
  search: string;
  categoryId: string;
  minPrice: string;
  maxPrice: string;
  location: string;
  store: string;
  type: string;
}

interface AdvancedSearchProps {
  onSearch: (filters: SearchFilters) => void;
  initialFilters?: Partial<SearchFilters>;
}

export function AdvancedSearch({ onSearch, initialFilters = {} }: AdvancedSearchProps) {
  const [isFiltersOpen, setIsFiltersOpen] = useState(false);
  const [filters, setFilters] = useState<SearchFilters>({
    search: initialFilters.search || "",
    categoryId: initialFilters.categoryId || "all",
    minPrice: initialFilters.minPrice || "",
    maxPrice: initialFilters.maxPrice || "",
    location: initialFilters.location || "",
    store: initialFilters.store || "",
    type: initialFilters.type || "all",
  });

  const { data: categories } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const handleFilterChange = (key: keyof SearchFilters, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    onSearch(filters);
  };

  const clearFilters = () => {
    const emptyFilters = {
      search: "",
      categoryId: "all",
      minPrice: "",
      maxPrice: "",
      location: "",
      store: "",
      type: "all",
    };
    setFilters(emptyFilters);
    onSearch(emptyFilters);
  };

  const hasActiveFilters = Object.values(filters).some(value => value !== "");
  const hasAdvancedFilters = (filters.categoryId && filters.categoryId !== "all") || filters.minPrice || filters.maxPrice || filters.location || filters.store || (filters.type && filters.type !== "all");

  const categoryTypes = [
    { value: "all", label: "Todos os tipos" },
    { value: "service", label: "ServiÃ§os" },
    { value: "product", label: "Produtos" },
    { value: "job", label: "Vagas" },
    { value: "news", label: "NotÃ­cias" },
  ];

  return (
    <Card className="mb-6">
      <CardContent className="p-4">
        <form onSubmit={handleSearch}>
          {/* Main Search Bar */}
          <div className="flex gap-3 mb-4">
            <div className="flex-1 relative">
              <Input
                type="text"
                placeholder="Busque por serviÃ§os, produtos, vagas..."
                value={filters.search}
                onChange={(e) => handleFilterChange("search", e.target.value)}
                className="pl-10 pr-4"
                data-testid="input-search"
              />
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            </div>

            <Button
              type="button"
              variant="outline"
              onClick={() => setIsFiltersOpen(!isFiltersOpen)}
              className={`flex items-center gap-2 ${hasAdvancedFilters ? 'border-purple-500 text-purple-600' : ''}`}
              data-testid="button-toggle-filters"
            >
              <Filter className="h-4 w-4" />
              Filtros
              {hasAdvancedFilters && (
                <span className="bg-purple-500 text-white text-xs px-2 py-1 rounded-full">
                  {Object.values(filters).filter(v => v !== "" && v !== filters.search).length}
                </span>
              )}
              <ChevronDown className={`h-4 w-4 transition-transform ${isFiltersOpen ? 'rotate-180' : ''}`} />
            </Button>

            <Button
              type="submit"
              className="bg-purple-600 hover:bg-purple-700 text-white px-8"
              data-testid="button-search"
            >
              Buscar
            </Button>
          </div>

          {/* Advanced Filters */}
          <Collapsible open={isFiltersOpen} onOpenChange={setIsFiltersOpen}>
            <CollapsibleContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4 bg-purple-50 rounded-lg">
                {/* Type Filter */}
                <div className="space-y-2">
                  <Label htmlFor="type-filter">Tipo</Label>
                  <Select 
                    value={filters.type} 
                    onValueChange={(value) => handleFilterChange("type", value)}
                  >
                    <SelectTrigger data-testid="select-type">
                      <SelectValue placeholder="Selecione o tipo" />
                    </SelectTrigger>
                    <SelectContent>
                      {categoryTypes.map((type) => (
                        <SelectItem key={type.value} value={type.value}>
                          {type.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Category Filter */}
                <div className="space-y-2">
                  <Label htmlFor="category-filter">Categoria</Label>
                  <Select 
                    value={filters.categoryId} 
                    onValueChange={(value) => handleFilterChange("categoryId", value)}
                  >
                    <SelectTrigger data-testid="select-category">
                      <SelectValue placeholder="Selecione a categoria" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todas as categorias</SelectItem>
                      {categories?.filter(cat => !filters.type || cat.type === filters.type).map((category) => (
                        <SelectItem key={category.id} value={category.id}>
                          {category.icon} {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Location Filter */}
                <div className="space-y-2">
                  <Label htmlFor="location-filter">LocalizaÃ§Ã£o</Label>
                  <Input
                    id="location-filter"
                    type="text"
                    placeholder="Ex: SÃ£o Paulo, SP"
                    value={filters.location}
                    onChange={(e) => handleFilterChange("location", e.target.value)}
                    data-testid="input-location"
                  />
                </div>

                {/* Price Range */}
                <div className="space-y-2">
                  <Label>Faixa de PreÃ§o (R$)</Label>
                  <div className="flex gap-2">
                    <Input
                      type="number"
                      placeholder="Min"
                      value={filters.minPrice}
                      onChange={(e) => handleFilterChange("minPrice", e.target.value)}
                      min="0"
                      step="0.01"
                      data-testid="input-min-price"
                    />
                    <Input
                      type="number"
                      placeholder="Max"
                      value={filters.maxPrice}
                      onChange={(e) => handleFilterChange("maxPrice", e.target.value)}
                      min="0"
                      step="0.01"
                      data-testid="input-max-price"
                    />
                  </div>
                </div>

                {/* Store Filter */}
                <div className="space-y-2">
                  <Label htmlFor="store-filter">Loja/Vendedor</Label>
                  <Input
                    id="store-filter"
                    type="text"
                    placeholder="Nome da loja ou vendedor"
                    value={filters.store}
                    onChange={(e) => handleFilterChange("store", e.target.value)}
                    data-testid="input-store"
                  />
                </div>

                {/* Clear Filters Button */}
                <div className="space-y-2">
                  <Label>&nbsp;</Label>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={clearFilters}
                    disabled={!hasActiveFilters}
                    className="w-full flex items-center gap-2"
                    data-testid="button-clear-filters"
                  >
                    <X className="h-4 w-4" />
                    Limpar Filtros
                  </Button>
                </div>
              </div>
            </CollapsibleContent>
          </Collapsible>
        </form>

        {/* Active Filters Display */}
        {hasAdvancedFilters && (
          <div className="mt-4 flex flex-wrap gap-2">
            <span className="text-sm text-gray-600">Filtros ativos:</span>
            {filters.type && filters.type !== "all" && (
              <span className="inline-flex items-center gap-1 px-2 py-1 bg-purple-100 text-purple-700 rounded-full text-xs">
                Tipo: {categoryTypes.find(t => t.value === filters.type)?.label}
                <X 
                  className="h-3 w-3 cursor-pointer" 
                  onClick={() => handleFilterChange("type", "all")}
                />
              </span>
            )}
            {filters.categoryId && filters.categoryId !== "all" && (
              <span className="inline-flex items-center gap-1 px-2 py-1 bg-purple-100 text-purple-700 rounded-full text-xs">
                Categoria: {categories?.find(c => c.id === filters.categoryId)?.name}
                <X 
                  className="h-3 w-3 cursor-pointer" 
                  onClick={() => handleFilterChange("categoryId", "all")}
                />
              </span>
            )}
            {filters.location && (
              <span className="inline-flex items-center gap-1 px-2 py-1 bg-purple-100 text-purple-700 rounded-full text-xs">
                Local: {filters.location}
                <X 
                  className="h-3 w-3 cursor-pointer" 
                  onClick={() => handleFilterChange("location", "")}
                />
              </span>
            )}
            {(filters.minPrice || filters.maxPrice) && (
              <span className="inline-flex items-center gap-1 px-2 py-1 bg-purple-100 text-purple-700 rounded-full text-xs">
                PreÃ§o: R$ {filters.minPrice || "0"} - R$ {filters.maxPrice || "âˆž"}
                <X 
                  className="h-3 w-3 cursor-pointer" 
                  onClick={() => {
                    handleFilterChange("minPrice", "");
                    handleFilterChange("maxPrice", "");
                  }}
                />
              </span>
            )}
            {filters.store && (
              <span className="inline-flex items-center gap-1 px-2 py-1 bg-purple-100 text-purple-700 rounded-full text-xs">
                Loja: {filters.store}
                <X 
                  className="h-3 w-3 cursor-pointer" 
                  onClick={() => handleFilterChange("store", "")}
                />
              </span>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/ImageUploader.tsx.content:
import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Upload, X, Image as ImageIcon } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ImageUploaderProps {
  onImagesChange: (imageUrls: string[]) => void;
  maxImages?: number;
  className?: string;
  disabled?: boolean;
}

export function ImageUploader({ onImagesChange, maxImages = 8, className = "", disabled = false }: ImageUploaderProps) {
  const [uploadedImages, setUploadedImages] = useState<string[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);

    if (files.length === 0) return;

    // Check if adding these files would exceed max images
    if (uploadedImages.length + files.length > maxImages) {
      toast({
        title: "Muitas imagens",
        description: `VocÃª pode adicionar no mÃ¡ximo ${maxImages} imagens`,
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);

    try {
      const newImageUrls: string[] = [];

      for (const file of files) {
        // Validate file type
        if (!file.type.startsWith('image/')) {
          toast({
            title: "Arquivo invÃ¡lido",
            description: `${file.name} nÃ£o Ã© uma imagem vÃ¡lida`,
            variant: "destructive",
          });
          continue;
        }

        // Check file size (max 10MB)
        if (file.size > 10 * 1024 * 1024) {
          toast({
            title: "Arquivo muito grande",
            description: `${file.name} excede o limite de 10MB`,
            variant: "destructive",
          });
          continue;
        }

        // Create form data for upload
        const formData = new FormData();
        formData.append('images', file);

        // Upload directly to server with form data
        const uploadResult = await fetch('/api/images/upload-simple', {
          method: 'POST',
          body: formData,
        });

        if (!uploadResult.ok) {
          throw new Error(`Falha no upload: ${uploadResult.statusText}`);
        }

        const response = await uploadResult.json();
        if (response.imageUrls && response.imageUrls.length > 0) {
          newImageUrls.push(...response.imageUrls);
        }
      }

      const updatedImages = [...uploadedImages, ...newImageUrls];
      setUploadedImages(updatedImages);
      onImagesChange(updatedImages);

      if (newImageUrls.length > 0) {
        toast({
          title: "Upload concluÃ­do!",
          description: `${newImageUrls.length} imagem(s) padronizada(s) e carregada(s) com sucesso`,
        });
      }

    } catch (error) {
      console.error('Upload error:', error);
      toast({
        title: "Erro no upload",
        description: "Ocorreu um erro ao fazer upload das imagens",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const processImageFile = async (file: File): Promise<File> => {
    return new Promise((resolve) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!;
      const img = new Image();

      img.onload = () => {
        // Mercado Livre style dimensions
        const maxWidth = 1200;
        const maxHeight = 900;

        let { width, height } = img;

        // Calculate new dimensions maintaining aspect ratio
        if (width > height) {
          if (width > maxWidth) {
            height = (height * maxWidth) / width;
            width = maxWidth;
          }
        } else {
          if (height > maxHeight) {
            width = (width * maxHeight) / height;
            height = maxHeight;
          }
        }

        canvas.width = width;
        canvas.height = height;

        // Draw and compress
        ctx.drawImage(img, 0, 0, width, height);

        canvas.toBlob(
          (blob) => {
            const processedFile = new File([blob!], file.name, {
              type: 'image/jpeg',
              lastModified: Date.now(),
            });
            resolve(processedFile);
          },
          'image/jpeg',
          0.85 // Quality similar to Mercado Livre
        );
      };

      img.src = URL.createObjectURL(file);
    });
  };

  const removeImage = (indexToRemove: number) => {
    const updatedImages = uploadedImages.filter((_, index) => index !== indexToRemove);
    setUploadedImages(updatedImages);
    onImagesChange(updatedImages);
  };

  const openFileSelector = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className={`space-y-4 ${className}`}>
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-medium text-gray-900">Imagens do AnÃºncio</h3>
        <span className="text-sm text-gray-500">
          {uploadedImages.length} de {maxImages} imagens
        </span>
      </div>

      {/* Upload Area */}
      <Card className="border-2 border-dashed border-gray-300 hover:border-primary-yellow transition-colors">
        <CardContent className="p-6">
          <div className="text-center">
            <Button
              type="button"
              variant="outline"
              onClick={openFileSelector}
              disabled={disabled || isUploading || uploadedImages.length >= maxImages}
              className="mb-4"
            >
              <Upload className="h-4 w-4 mr-2" />
              {isUploading ? 'Fazendo upload...' : 'Adicionar Imagens'}
            </Button>

            <p className="text-sm text-gray-600 mb-2">
              Clique para selecionar imagens ou arraste aqui
            </p>
            <p className="text-xs text-gray-500">
              Formatos aceitos: JPG, PNG, WebP â€¢ MÃ¡ximo 10MB por imagem
              <br />
              âœ¨ As imagens serÃ£o automaticamente padronizadas para 800x600px com qualidade otimizada
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*"
        multiple
        onChange={handleFileSelect}
        className="hidden"
        data-testid="file-input"
      />

      {/* Image Preview Grid */}
      {uploadedImages.length > 0 && (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {uploadedImages.map((imageUrl, index) => (
            <div key={index} className="relative group">
              <Card className="overflow-hidden">
                <div className="aspect-square relative bg-gray-100">
                  <img
                    src={imageUrl}
                    alt={`Upload ${index + 1}`}
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      const target = e.target as HTMLImageElement;
                      target.src = 'data:image/svg+xml;base64,';
                    }}
                  />

                  {/* Remove button */}
                  <Button
                    type="button"
                    variant="destructive"
                    size="sm"
                    className="absolute top-1 right-1 h-6 w-6 p-0 opacity-0 group-hover:opacity-100 transition-opacity"
                    onClick={() => removeImage(index)}
                    data-testid={`remove-image-${index}`}
                  >
                    <X className="h-3 w-3" />
                  </Button>

                  {/* Image number badge */}
                  <div className="absolute bottom-1 left-1 bg-black/50 text-white text-xs px-2 py-1 rounded">
                    {index + 1}
                  </div>
                </div>
              </Card>
            </div>
          ))}
        </div>
      )}

      {/* Tips */}
      {uploadedImages.length === 0 && (
        <Card className="bg-blue-50 border-blue-200">
          <CardContent className="p-4">
            <div className="flex items-start space-x-3">
              <ImageIcon className="h-5 w-5 text-blue-500 mt-0.5" />
              <div className="text-sm text-blue-700">
                <p className="font-medium mb-1">Dicas para melhores fotos:</p>
                <ul className="space-y-1 text-xs">
                  <li>â€¢ Use boa iluminaÃ§Ã£o natural</li>
                  <li>â€¢ Tire fotos de diferentes Ã¢ngulos</li>
                  <li>â€¢ A primeira imagem serÃ¡ a capa do anÃºncio</li>
                  <li>â€¢ Imagens nÃ­tidas vendem mais</li>
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/ObjectUploader.tsx.content:
import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A file upload component that renders as a button and provides a modal interface for
 * file management.
 * 
 * Features:
 * - Renders as a customizable button that opens a file upload modal
 * - Provides a modal interface for:
 *   - File selection
 *   - File preview
 *   - Upload progress tracking
 *   - Upload status display
 * 
 * The component uses Uppy under the hood to handle all file upload functionality.
 * All file management features are automatically handled by the Uppy dashboard modal.
 * 
 * @param props - Component props
 * @param props.maxNumberOfFiles - Maximum number of files allowed to be uploaded
 *   (default: 1)
 * @param props.maxFileSize - Maximum file size in bytes (default: 10MB)
 * @param props.onGetUploadParameters - Function to get upload parameters (method and URL).
 *   Typically used to fetch a presigned URL from the backend server for direct-to-S3
 *   uploads.
 * @param props.onComplete - Callback function called when upload is complete. Typically
 *   used to make post-upload API calls to update server state and set object ACL
 *   policies.
 * @param props.buttonClassName - Optional CSS class name for the button
 * @param props.children - Content to be rendered inside the button
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
      })
  );

  return (
    <div>
      <Button onClick={() => setShowModal(true)} className={buttonClassName}>
        {children}
      </Button>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
      />
    </div>
  );
}

client/src/components/PostCard.tsx.content:
import { useState, useEffect } from "react";
import { Link } from "wouter";
import { Heart, MessageCircle, Eye, MapPin, Phone, ExternalLink, ThumbsUp, Zap } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";

interface PostCardProps {
  post: {
    id: string;
    title: string;
    description: string;
    price?: number | null;
    location: string | null;
    imageUrls?: string[] | null;
    whatsappNumber?: string | null;
    externalLink?: string | null;
    viewCount: number;
    likesCount: number;
    commentsCount: number;
    createdAt: string;
    isFeatured?: boolean;
    category: {
      id: string;
      name: string;
      icon: string;
      type: string;
    };
    user: {
      id: string;
      name: string;
      avatar?: string | null;
    };
  };
}

export function PostCard({ post }: PostCardProps) {
  const { isAuthenticated, user } = useAuth();
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const [localLiked, setLocalLiked] = useState(false);
  const [localFavorited, setLocalFavorited] = useState(false);
  const [localLikesCount, setLocalLikesCount] = useState(post.likesCount);

  // Get like/favorite status
  const { data: likeStatus } = useQuery<{liked: boolean; favorited: boolean}>({
    queryKey: [`/api/posts/${post.id}/like-status`],
    enabled: isAuthenticated,
  });

  useEffect(() => {
    if (likeStatus) {
      setLocalLiked(likeStatus.liked);
      setLocalFavorited(likeStatus.favorited);
    }
  }, [likeStatus]);

  const likeMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", `/api/posts/${post.id}/like`);
    },
    onMutate: async () => {
      // Optimistic update
      const newLiked = !localLiked;
      setLocalLiked(newLiked);
      setLocalLikesCount(prev => newLiked ? prev + 1 : prev - 1);
      return { previousLiked: localLiked, previousCount: localLikesCount };
    },
    onError: (err, variables, context) => {
      // Rollback on error
      if (context) {
        setLocalLiked(context.previousLiked);
        setLocalLikesCount(context.previousCount);
      }
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/posts/${post.id}/like-status`] });
    },
  });

  const favoriteMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", `/api/posts/${post.id}/favorite`);
    },
    onMutate: async () => {
      // Optimistic update
      setLocalFavorited(!localFavorited);
      return { previousFavorited: localFavorited };
    },
    onSuccess: async (response) => {
      // Parse response to get data
      const data = await response.json();

      // Show success notification
      if (data.favorited) {
        toast({
          title: "Favorito adicionado!",
          description: `O anÃºncio "${post.title}" foi adicionado aos seus favoritos.`,
          duration: 3000,
        });
      } else {
        toast({
          title: "Favorito removido",
          description: `O anÃºncio "${post.title}" foi removido dos seus favoritos.`,
          duration: 3000,
        });
      }
    },
    onError: (err, variables, context) => {
      // Rollback on error
      if (context) {
        setLocalFavorited(context.previousFavorited);
      }
      toast({
        title: "Erro",
        description: "NÃ£o foi possÃ­vel atualizar seus favoritos. Tente novamente.",
        variant: "destructive",
        duration: 3000,
      });
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/posts/${post.id}/like-status`] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/favorites"] });
    },
  });

  const handleLike = () => {
    if (!isAuthenticated) return;
    likeMutation.mutate();
  };

  const handleFavorite = () => {
    if (!isAuthenticated) return;
    favoriteMutation.mutate();
  };

  const handleContact = async (type: 'whatsapp' | 'external') => {
    try {
      await apiRequest("POST", `/api/posts/${post.id}/contact`);

      if (type === 'whatsapp' && post.whatsappNumber) {
        const message = encodeURIComponent(`OlÃ¡! Vi seu anÃºncio "${post.title}" no CapyConnect e gostaria de mais informaÃ§Ãµes.`);
        const whatsappUrl = `https://wa.me/55${post.whatsappNumber.replace(/\D/g, '')}?text=${message}`;
        window.open(whatsappUrl, '_blank');
      } else if (type === 'external' && post.externalLink) {
        window.open(post.externalLink, '_blank');
      }
    } catch (error) {
      console.error('Error tracking contact:', error);
    }
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(price / 100);
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - date.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return 'Hoje';
    if (diffDays === 2) return 'Ontem';
    if (diffDays <= 7) return `${diffDays} dias atrÃ¡s`;

    return date.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  return (
    <Card className="overflow-hidden hover:shadow-lg transition-all duration-200 group">
      <div className="relative">
        {/* Featured Badge */}
        {post.isFeatured && (
          <Badge className="absolute top-2 left-2 z-10 bg-yellow-500 text-white text-xs">
            <Zap className="h-3 w-3 mr-1" />
            <span className="hidden sm:inline">Impulsionado</span>
            <span className="sm:hidden">âš¡</span>
          </Badge>
        )}

        {/* Image */}
        <Link href={`/post/${post.id}`} className="block">
          <div className="aspect-[4/3] sm:aspect-[4/3] bg-gray-200 overflow-hidden">
            {post.imageUrls && post.imageUrls[0] ? (
              <img
                src={post.imageUrls[0]}
                alt={post.title}
                className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-200"
                onError={(e) => {
                  console.error("Erro ao carregar imagem:", post.imageUrls![0]);
                  e.currentTarget.src = 'data:image/svg+xml;base64,';
                }}
              />
            ) : (
              <div className="w-full h-full flex items-center justify-center bg-gray-100">
                <div className="text-center">
                  <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-2">
                    <span className="text-2xl">{post.category.icon}</span>
                  </div>
                  <p className="text-sm text-gray-500">Sem imagem</p>
                </div>
              </div>
            )}
          </div>
        </Link>
      </div>

      <CardContent className="p-4 sm:p-4">
        {/* Category Badge */}
        <div className="flex items-center justify-between mb-3">
          <Badge variant="outline" className="text-xs">
            <span className="mr-1">{post.category.icon}</span>
            {post.category.name}
          </Badge>
          <span className="text-xs text-gray-500">{formatDate(post.createdAt)}</span>
        </div>

        {/* Title and Description */}
        <Link href={`/post/${post.id}`} className="block mb-3">
          <h3 className="font-semibold text-base sm:text-lg line-clamp-2 group-hover:text-primary-yellow transition-colors">
            {post.title}
          </h3>
          <p className="text-gray-600 text-xs sm:text-sm line-clamp-2 mt-1">
            {post.description}
          </p>
        </Link>

        {/* Price */}
        {post.price && (
          <div className="mb-3">
            <span className="text-xl sm:text-2xl font-bold text-green-600">
              {formatPrice(post.price)}
            </span>
          </div>
        )}

        {/* Location */}
        {post.location && (
          <div className="flex items-center text-sm text-gray-500 mb-3">
            <MapPin className="h-4 w-4 mr-1" />
            <span>{post.location}</span>
          </div>
        )}

        {/* User */}
        <div className="flex items-center mb-4">
          <div className="w-6 h-6 bg-primary-yellow rounded-full flex items-center justify-center text-xs font-medium text-white mr-2">
            {post.user.name.charAt(0).toUpperCase()}
          </div>
          <span className="text-sm text-gray-600">{post.user.name}</span>
        </div>

        {/* Actions */}
        <div className="space-y-2">
          {/* Contact Buttons */}
          <div className="flex flex-col sm:flex-row gap-2">
            {post.whatsappNumber && (
              <Button
                size="sm"
                className="flex-1 bg-green-500 hover:bg-green-600 text-white text-sm py-2.5 px-4"
                onClick={() => handleContact('whatsapp')}
              >
                <Phone className="h-4 w-4 mr-2" />
                WhatsApp
              </Button>
            )}
            {post.externalLink && (
              <Button
                size="sm"
                variant="outline"
                className="flex-1 text-sm py-2.5 px-4"
                onClick={() => handleContact('external')}
              >
                <ExternalLink className="h-4 w-4 mr-2" />
                Site
              </Button>
            )}
            {/* If user owns this post, show boost button */}
            {user && user.id === post.user.id && (
              <Link href={`/post/${post.id}/impulsionar`}>
                <Button
                  size="sm"
                  className="bg-yellow-500 hover:bg-yellow-600 text-white text-xs sm:text-sm"
                >
                  <Zap className="h-3 w-3 sm:h-4 sm:w-4 mr-2" />
                  <span className="hidden sm:inline">Impulsionar</span>
                  <span className="sm:hidden">âš¡</span>
                </Button>
              </Link>
            )}
          </div>

          {/* Stats and Actions */}
          <div className="flex items-center justify-between pt-2 border-t">
            <div className="flex items-center gap-4 text-sm text-gray-500">
              <span className="flex items-center gap-1">
                <Eye className="h-4 w-4" />
                {post.viewCount}
              </span>
              <span className="flex items-center gap-1">
                <MessageCircle className="h-4 w-4" />
                {post.commentsCount}
              </span>
            </div>

            <div className="flex items-center gap-2">
              <Button
                size="sm"
                variant="ghost"
                onClick={handleLike}
                disabled={!isAuthenticated}
                className={`flex items-center gap-1 transition-colors ${
                  localLiked 
                    ? 'text-blue-600 bg-blue-50 hover:bg-blue-100' 
                    : 'text-gray-500 hover:text-blue-600 hover:bg-blue-50'
                }`}
              >
                <ThumbsUp className={`h-4 w-4 ${localLiked ? 'fill-current' : ''}`} />
                <span className="text-sm">{localLikesCount}</span>
              </Button>

              <Button
                size="sm"
                variant="ghost"
                onClick={handleFavorite}
                disabled={!isAuthenticated}
                className={`flex items-center gap-1 transition-colors ${
                  localFavorited 
                    ? 'text-red-600 bg-red-50 hover:bg-red-100' 
                    : 'text-gray-500 hover:text-red-600 hover:bg-red-50'
                }`}
              >
                <Heart className={`h-4 w-4 ${localFavorited ? 'fill-current' : ''}`} />
              </Button>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

