import { ImageUploader } from "@/components/ImageUploader";
import { X } from "lucide-react";

const createPostSchema = z.object({
  title: z.string().min(10, "TÃ­tulo deve ter pelo menos 10 caracteres"),
  description: z.string().min(30, "DescriÃ§Ã£o deve ter pelo menos 30 caracteres"),
  categoryId: z.string().min(1, "Selecione uma categoria"),
  price: z.string().optional(),
  whatsappNumber: z.string().optional(),
  externalLink: z.string().url("URL invÃ¡lida").optional().or(z.literal("")),
  location: z.string().min(5, "LocalizaÃ§Ã£o deve ter pelo menos 5 caracteres"),
});

type CreatePostFormData = z.infer<typeof createPostSchema>;

export default function CreatePost() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedType, setSelectedType] = useState<string>("service");
  const [uploadedImages, setUploadedImages] = useState<string[]>([]);
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user, isAuthenticated, isLoading } = useAuth();

  const handleImagesChange = (imageUrls: string[]) => {
    setUploadedImages(imageUrls);
  };

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      setLocation("/login");
    }
  }, [isAuthenticated, isLoading, setLocation]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-yellow"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  const { data: categories, isLoading: categoriesLoading } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const form = useForm<CreatePostFormData>({
    resolver: zodResolver(createPostSchema),
    defaultValues: {
      title: "",
      description: "",
      categoryId: "",
      price: "",
      whatsappNumber: "",
      externalLink: "",
      location: "",
    },
  });

  const createPostMutation = useMutation({
    mutationFn: async (postData: InsertPost) => {
      return apiRequest("POST", "/api/posts", postData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      toast({
        title: "Post criado com sucesso!",
        description: "Seu anÃºncio foi publicado no marketplace.",
      });
      setLocation("/");
    },
    onError: () => {
      toast({
        title: "Erro ao criar post",
        description: "NÃ£o foi possÃ­vel publicar seu anÃºncio. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = async (data: CreatePostFormData) => {
    if (!user) return;

    try {
      const postData: InsertPost = {
        title: data.title,
        description: data.description,
        categoryId: data.categoryId,
        imageUrls: uploadedImages.length > 0 ? uploadedImages : null,
        price: data.price ? parseInt(data.price.replace(/\D/g, '')) : null,
        whatsappNumber: data.whatsappNumber || null,
        externalLink: data.externalLink || null,
        location: data.location,
        userId: user.id,
        isActive: true,
        isFeatured: false,
        expiresAt: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days
      };

      createPostMutation.mutate(postData);
    } catch (error) {
      toast({
        title: "Erro ao criar post",
        description: "NÃ£o foi possÃ­vel criar o post. Tente novamente.",
        variant: "destructive",
      });
    }
  };

  const categoriesByType = categories?.filter(cat => cat.type === selectedType) || [];

  if (categoriesLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-yellow"></div>
      </div>
    );
  }

  const getTypeInfo = (type: string) => {
    switch (type) {
      case 'service':
        return {
          title: 'Postar ServiÃ§o',
          description: 'OfereÃ§a seus serviÃ§os profissionais',
          icon: 'ðŸ”§',
          priceLabel: 'PreÃ§o do serviÃ§o',
          placeholder: 'Ex: Limpeza residencial completa com produtos inclusos...'
        };
      case 'product':
        return {
          title: 'Vender Produto',
          description: 'Venda seus produtos',
          icon: 'ðŸ“¦',
          priceLabel: 'PreÃ§o de venda',
          placeholder: 'Ex: Notebook gamer em Ã³timo estado, usado por apenas 6 meses...'
        };
      case 'job':
        return {
          title: 'Postar Vaga',
          description: 'Divulgue vagas de emprego',
          icon: 'ðŸ’¼',
          priceLabel: 'SalÃ¡rio (opcional)',
          placeholder: 'Ex: Vaga para desenvolvedor frontend com experiÃªncia em React...'
        };
      case 'news':
        return {
          title: 'Publicar NotÃ­cia',
          description: 'Compartilhe notÃ­cias relevantes',
          icon: 'ðŸ“°',
          priceLabel: '',
          placeholder: 'Ex: Mercado de startups registra crescimento de 40% no Brasil...'
        };
      default:
        return {
          title: 'Criar Post',
          description: 'Publique seu conteÃºdo',
          icon: 'ðŸ“',
          priceLabel: 'PreÃ§o',
          placeholder: 'Descreva seu post...'
        };
    }
  };

  const typeInfo = getTypeInfo(selectedType);

  const formatPrice = (value: string) => {
    const numbers = value.replace(/\D/g, '');
    const cents = parseInt(numbers) || 0;
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(cents / 100);
  };





  return (
    <div className="min-h-screen bg-gray-50">
      <Header searchQuery={searchQuery} onSearchChange={setSearchQuery} />

      <main className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-gray-800 mb-2">Publicar no Marketplace</h1>
            <p className="text-gray-600">Escolha o tipo de conteÃºdo que deseja publicar</p>
          </div>

          {/* Type Selection */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
            {[
              { type: 'service', label: 'ServiÃ§os', icon: 'ðŸ”§' },
              { type: 'product', label: 'Produtos', icon: 'ðŸ“¦' },
              { type: 'job', label: 'Vagas', icon: 'ðŸ’¼' },
              { type: 'news', label: 'NotÃ­cias', icon: 'ðŸ“°' },
            ].map((item) => (
              <Card 
                key={item.type}
                className={`cursor-pointer transition-all ${
                  selectedType === item.type 
                    ? 'ring-2 ring-primary-yellow bg-light-yellow' 
                    : 'hover:shadow-md'
                }`}
                onClick={() => setSelectedType(item.type)}
              >
                <CardContent className="p-4 text-center">
                  <div className="text-2xl mb-2">{item.icon}</div>
                  <span className="font-medium text-gray-800">{item.label}</span>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Form */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <span>{typeInfo.icon}</span>
                <span>{typeInfo.title}</span>
              </CardTitle>
              <CardDescription>{typeInfo.description}</CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  {/* Title */}
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>TÃ­tulo *</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder={selectedType === 'job' ? 'Ex: Desenvolvedor Frontend - React' : 'Ex: Limpeza Residencial Profissional'}
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Description */}
                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>DescriÃ§Ã£o *</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder={typeInfo.placeholder}
                            rows={4}
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Category */}
                  <FormField
                    control={form.control}
                    name="categoryId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Categoria *</FormLabel>
                        <FormControl>
                          <Select onValueChange={field.onChange} value={field.value || ""}>
                            <SelectTrigger>
                              <SelectValue placeholder="Selecione uma categoria" />
                            </SelectTrigger>
                            <SelectContent>
                              {categoriesByType?.map((category) => (
                                <SelectItem key={category.id} value={category.id}>
                                  {category.icon} {category.name}
                                </SelectItem>
                              )) || []}
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Image Upload Section */}
                  <div className="space-y-4">
                    <div>
                      <FormLabel>Imagens (atÃ© 3)</FormLabel>
                      <p className="text-sm text-gray-600 mb-3">
                        Adicione atÃ© 3 imagens. Elas serÃ£o automaticamente redimensionadas e otimizadas.
                      </p>
                    </div>

                    <ImageUploader
                      onImagesChange={handleImagesChange}
                      maxImages={3}
                      disabled={createPostMutation.isPending}
                    />
                  </div>

                  {/* Price (if not news) */}
                  {selectedType !== 'news' && (
                    <FormField
                      control={form.control}
                      name="price"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>{typeInfo.priceLabel}</FormLabel>
                          <FormControl>
                            <Input 
