import { useState, useEffect } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { ArrowLeft, ImagePlus, X, Upload } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import Header from "@/components/header";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import type { Category, Post } from "@shared/schema";

const editPostSchema = z.object({
  title: z.string().min(1, "TÃ­tulo Ã© obrigatÃ³rio").max(100, "TÃ­tulo muito longo"),
  description: z.string().min(1, "DescriÃ§Ã£o Ã© obrigatÃ³ria").max(1000, "DescriÃ§Ã£o muito longa"),
  categoryId: z.string().min(1, "Categoria Ã© obrigatÃ³ria"),
  price: z.coerce.number().optional(),
  location: z.string().min(1, "LocalizaÃ§Ã£o Ã© obrigatÃ³ria"),
  whatsappNumber: z.string().optional(),
  externalLink: z.string().url("URL invÃ¡lida").optional().or(z.literal("")),
});

type EditPostData = z.infer<typeof editPostSchema>;

export default function EditarAnuncio() {
  const params = useParams();
  const [, setLocation] = useLocation();
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [images, setImages] = useState<string[]>([]);
  const [uploading, setUploading] = useState(false);

  const postId = params.id;

  // Fetch post data
  const { data: post, isLoading: postLoading } = useQuery<Post>({
    queryKey: [`/api/posts/${postId}`],
    enabled: !!postId && isAuthenticated,
  });

  // Fetch categories
  const { data: categories } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const form = useForm<EditPostData>({
    resolver: zodResolver(editPostSchema),
    defaultValues: {
      title: "",
      description: "",
      categoryId: "",
      price: undefined,
      location: "",
      whatsappNumber: "",
      externalLink: "",
    },
  });

  // Update form when post data loads
  useEffect(() => {
    if (post) {
      form.reset({
        title: post.title,
        description: post.description,
        categoryId: post.categoryId,
        price: post.price ? post.price / 100 : undefined, // Convert from cents
        location: post.location,
        whatsappNumber: post.whatsappNumber ?? "",
        externalLink: post.externalLink ?? "",
      });
      setImages(post.imageUrls || []);
    }
  }, [post, form]);

  const updatePostMutation = useMutation({
    mutationFn: async (data: EditPostData) => {
      const postData = {
        ...data,
        price: data.price ? Math.round(data.price * 100) : null, // Convert to cents
        imageUrls: images,
      };
      return apiRequest("PUT", `/api/posts/${postId}`, postData);
    },
    onSuccess: () => {
      toast({
        title: "AnÃºncio atualizado!",
        description: "Suas alteraÃ§Ãµes foram salvas com sucesso.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user/posts"] });
      queryClient.invalidateQueries({ queryKey: [`/api/posts/${postId}`] });
      setLocation("/meus-anuncios");
    },
    onError: (error: any) => {
      toast({
        title: "Erro ao atualizar anÃºncio",
        description: error.message || "Tente novamente mais tarde.",
        variant: "destructive",
      });
    },
  });

  const handleImageUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;

    if (images.length + files.length > 8) {
      toast({
        title: "Limite de imagens",
        description: "VocÃª pode adicionar no mÃ¡ximo 8 imagens.",
        variant: "destructive",
      });
      return;
    }

    setUploading(true);
    const formData = new FormData();
    Array.from(files).forEach((file) => {
      formData.append("images", file);
    });

    try {
      const response = await fetch("/api/images/upload-simple", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Falha no upload");
      }

      const result = await response.json();
      setImages(prev => [...prev, ...result.imageUrls]);

      toast({
        title: "Imagens enviadas!",
        description: `${result.imageUrls.length} imagem(ns) padronizada(s) e adicionada(s) com sucesso.`,
      });
    } catch (error) {
      toast({
        title: "Erro no upload",
        description: "NÃ£o foi possÃ­vel enviar as imagens. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  const removeImage = (index: number) => {
    setImages(prev => prev.filter((_, i) => i !== index));
  };

  const onSubmit = (data: EditPostData) => {
    updatePostMutation.mutate(data);
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Acesso negado</h2>
          <p className="text-gray-600">VocÃª precisa estar logado para editar anÃºncios.</p>
        </div>
      </div>
    );
  }

  if (postLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header searchQuery="" onSearchChange={() => {}} />
        <div className="container mx-auto px-4 py-8">
          <div className="max-w-2xl mx-auto">
            <div className="bg-white rounded-lg shadow-sm p-6 animate-pulse">
              <div className="h-8 bg-gray-200 rounded mb-4"></div>
              <div className="h-4 bg-gray-200 rounded mb-2"></div>
              <div className="h-4 bg-gray-200 rounded w-3/4"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!post) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header searchQuery="" onSearchChange={() => {}} />
        <div className="container mx-auto px-4 py-8">
          <div className="max-w-2xl mx-auto text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">AnÃºncio nÃ£o encontrado</h2>
            <p className="text-gray-600 mb-4">O anÃºncio que vocÃª estÃ¡ tentando editar nÃ£o existe.</p>
            <Button onClick={() => setLocation("/meus-anuncios")}>
              Voltar para meus anÃºncios
            </Button>
          </div>
        </div>
      </div>
    );
  }

  // Check if user owns this post
  if (post.userId !== user?.id) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header searchQuery="" onSearchChange={() => {}} />
        <div className="container mx-auto px-4 py-8">
          <div className="max-w-2xl mx-auto text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Acesso negado</h2>
            <p className="text-gray-600 mb-4">VocÃª sÃ³ pode editar seus prÃ³prios anÃºncios.</p>
            <Button onClick={() => setLocation("/meus-anuncios")}>
              Voltar para meus anÃºncios
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header searchQuery="" onSearchChange={() => {}} />

      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          {/* Header */}
          <div className="flex items-center space-x-4 mb-8">
            <Button
              variant="ghost"
              onClick={() => setLocation("/meus-anuncios")}
              className="p-2"
              data-testid="button-back"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Editar AnÃºncio</h1>
              <p className="text-gray-600">FaÃ§a as alteraÃ§Ãµes necessÃ¡rias no seu anÃºncio</p>
            </div>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>InformaÃ§Ãµes do AnÃºncio</CardTitle>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  {/* Title */}
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>TÃ­tulo *</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Ex: iPhone 13 Pro Max seminovo"
                            {...field}
                            data-testid="input-title"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Category */}
