    createPostMutation.mutate(postData);
  };

  const getTypeInfo = (type: string) => {
    switch (type) {
      case 'service':
        return {
          title: 'Oferecer ServiÃ§o',
          description: 'Divulgue seus serviÃ§os profissionais',
          icon: 'ðŸ”§',
          priceLabel: 'PreÃ§o (opcional)',
          placeholder: 'Ex: ServiÃ§o de limpeza residencial com produtos prÃ³prios...'
        };
      case 'product':
        return {
          title: 'Vender Produto',
          description: 'Anuncie produtos novos ou usados',
          icon: 'ðŸ“¦',
          priceLabel: 'PreÃ§o *',
          placeholder: 'Ex: Smartphone Samsung Galaxy em perfeito estado...'
        };
      case 'job':
        return {
          title: 'Postar Vaga',
          description: 'Divulgue vagas de emprego',
          icon: 'ðŸ’¼',
          priceLabel: 'SalÃ¡rio (opcional)',
          placeholder: 'Ex: Vaga para desenvolvedor frontend com experiÃªncia em React...'
        };
      case 'news':
        return {
          title: 'Publicar NotÃ­cia',
          description: 'Compartilhe notÃ­cias relevantes',
          icon: 'ðŸ“°',
          priceLabel: '',
          placeholder: 'Ex: Mercado de startups registra crescimento de 40% no Brasil...'
        };
      default:
        return {
          title: 'Criar Post',
          description: 'Publique seu conteÃºdo',
          icon: 'ðŸ“',
          priceLabel: 'PreÃ§o',
          placeholder: 'Descreva seu post...'
        };
    }
  };

  const typeInfo = getTypeInfo(selectedType);

  const formatPrice = (value: string) => {
    const numbers = value.replace(/\D/g, '');
    const cents = parseInt(numbers) || 0;
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
